                }
            }else{
                res.status(500).send({
                    message: 'EL USUARIO INGRESADO NO EXISTE',
                });
            }
        } catch (error) {
            console.log(error);
            res.status(500).send({
                message: 'HUBO UN ERROR',
            });
        }
    },
}

